import "./App.css";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:5000"; // Backend URL

function App() {
    const [projects, setProjects] = useState([]);
    const [newProject, setNewProject] = useState("");
    const [error, setError] = useState("");
    const [count, setCount] = useState(0);

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = async () => {
        try {
            const response = await fetch(`${API_URL}/api/projects`);
            const data = await response.json();
            setProjects(data.projects);
        } catch (error) {
            console.error("Error fetching projects:", error);
        }
    };

    useEffect(() => {
        fetch(`${API_URL}/api/projects/count`)
            .then((res) => res.json())
            .then((data) => setCount(data.total))
            .catch((error) => console.error("Error fetching count:", error));
    }, [projects]);

    const addProject = async () => {
        if (newProject.length < 3) {
            setError("Project name must be at least 3 characters");
            return;
        }

        try {
            const response = await fetch(`${API_URL}/api/projects`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name: newProject }),
            });

            const data = await response.json();

            if (data.status === "error") {
                setError(data.message);
            } else {
                setProjects([...projects, data.project]);
                setNewProject("");
                setError("");
            }
        } catch (error) {
            console.error("Failed to add project:", error);
        }
    };

    const deleteProject = async (id) => {
        try {
            const response = await fetch(`${API_URL}/api/projects/${id}`, {
                method: "DELETE",
            });

            const data = await response.json();
            if (data.status === "success") {
                setProjects(projects.filter((proj) => proj.id !== id));
            }
        } catch (error) {
            console.error("Failed to delete project:", error);
        }
    };

    return (
        <div className="App">
            <header className="App-header">

                <h1>ðŸš€ Project Manager</h1>
                <h2>Total Projects: <span className="count">{count}</span></h2>

                <div className="input-container">
                    <input
                        type="text"
                        placeholder="Enter project name..."
                        value={newProject}
                        onChange={(e) => setNewProject(e.target.value)}
                        className="project-input"
                    />
                    <button onClick={addProject} className="add-btn">Add</button>
                </div>

                {error && <p className="error">{error}</p>}

                <div className="project-list">
                    {projects.map((project) => (
                        <div key={project.id} className="project-card">
                            <span>{project.name}</span>
                            <button onClick={() => deleteProject(project.id)} className="delete-btn">ðŸ—‘</button>
                        </div>
                    ))}
                </div>
            </header>
        </div>
    );
}

export default App;
