import "./App.css";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:5000"; // Backend URL

function App() {
    const [projects, setProjects] = useState([]);
    const [newProject, setNewProject] = useState("");
    const [error, setError] = useState("");

    // Fetch projects using an arrow function
    const fetchProjects = async () => {
        try {
            const response = await fetch(`${API_URL}/api/projects`);
            const data = await response.json();

            if (!Array.isArray(data.projects)) {
                console.error("Invalid response format:", data);
                setProjects([]);
                return;
            }

            setProjects(data.projects);
        } catch (error) {
            console.error("Error fetching projects:", error);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    const addProject = async () => {
        if (newProject.trim().length < 3) {
            setError("Project name must be at least 3 characters");
            return;
        }

        try {
            const response = await fetch(`${API_URL}/api/projects`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name: newProject.trim() }),
            });

            const data = await response.json();
            console.log("Response from server:", data);  // ‚úÖ Debugging log

            if (data.status === "error") {
                setError(data.message);
            } else {
                setNewProject("");  
                setError("");  
                await fetchProjects();  // ‚úÖ Fetch updated projects from backend
            }
        } catch (error) {
            console.error("Failed to add project:", error);
        }
    };


    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            addProject();
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>
                    <span className="icon">‚öôÔ∏è</span> Project Manager
                </h1>

                <h2>Total Projects: <span className="count">{projects.length}</span></h2>

                {/* Input text box */}
                <div className="input-container">
                    <input
                        type="text"
                        placeholder="Enter project name..."
                        value={newProject}
                        onChange={(e) => setNewProject(e.target.value)}
                        onKeyDown={handleKeyPress} // Pressing Enter adds project
                        className="project-input"
                    />
                </div>

                {/* Buttons (Add Project & Refresh Projects) placed below input */}
                <div className="button-container">
                    <button onClick={addProject} className="add-btn">Add Project</button> 
                    <button onClick={() => fetchProjects()} className="refresh-btn">Refresh Projects</button> {/* Arrow function */}
                </div>

                {error && <p className="error">{error}</p>}

                <div className="project-list">
                    {projects.length === 0 ? (
                        <p>No projects found.</p>
                    ) : (
                        projects.map((project) => (
                            <div key={project.id} className="project-card">
                                <span>{project.name}</span>
                                <button onClick={() => deleteProject(project.id)} className="delete-btn">üóë</button>
                            </div>
                        ))
                    )}
                </div>
            </header>
        </div>
    );
}

export default App;
