import "./App.css";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:5000"; // Backend URL

function App() {
    const [projects, setProjects] = useState([]);
    const [newProject, setNewProject] = useState("");
    const [error, setError] = useState("");

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = async () => {
        try {
            const response = await fetch(`${API_URL}/api/projects`);
            const data = await response.json();

            if (!Array.isArray(data.projects)) {
                console.error("Invalid response format:", data);
                setProjects([]);
                return;
            }

            setProjects(data.projects);
        } catch (error) {
            console.error("Error fetching projects:", error);
        }
    };

    const addProject = async () => {
        if (newProject.trim().length < 3) {
            setError("Project name must be at least 3 characters");
            return;
        }

        try {
            const response = await fetch(`${API_URL}/api/projects`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name: newProject.trim() }),
            });

            const data = await response.json();

            if (data.status === "error") {
                setError(data.message);
            } else {
                setProjects([...projects, data.project]);
                setNewProject("");
                setError("");
            }
        } catch (error) {
            console.error("Failed to add project:", error);
        }
    };

    const deleteProject = async (id) => {
        try {
            const response = await fetch(`${API_URL}/api/projects/${id}`, {
                method: "DELETE",
            });

            const data = await response.json();
            if (data.status === "success") {
                setProjects(projects.filter((proj) => proj.id !== id));
            }
        } catch (error) {
            console.error("Failed to delete project:", error);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            addProject();
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>
                    <span className="icon">‚öôÔ∏è</span> Project Manager
                </h1>

                <h2>Total Projects: <span className="count">{projects.length}</span></h2>

                <div className="input-container">
                    <input
                        type="text"
                        placeholder="Enter project name..."
                        value={newProject}
                        onChange={(e) => setNewProject(e.target.value)}
                        onKeyDown={handleKeyPress} // Pressing Enter adds project
                        className="project-input"
                    />
                    <button onClick={addProject} className="add-btn">Add</button> 
                    <button onClick={fetchProjects} className="refresh-btn">Refresh</button> {/* Refresh button */}
                </div>

                {error && <p className="error">{error}</p>}

                <div className="project-list">
                    {projects.length === 0 ? (
                        <p>No projects found.</p>
                    ) : (
                        projects.map((project) => (
                            <div key={project.id} className="project-card">
                                <span>{project.name}</span>
                                <button onClick={() => deleteProject(project.id)} className="delete-btn">üóë</button>
                            </div>
                        ))
                    )}
                </div>
            </header>
        </div>
    );
}

export default App;
